sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }

		var whatneeded = g_form.getValue('u_choice_4');

   //Clear all of the choice from what needed field choice list
		g_form.clearOptions('u_choice_4');

	//if the value of the Request type field is hr, add
	//two hr choices and other to the what needed field choice list
	if(newValue == 'HR')
		{
			g_form.addOption('u_choice_4','hr1','Human Resource 1');
			g_form.addOption('u_choice_4','hr2','Human Resource 2');
			g_form.addOption('u_choice_4','other','Other');
		}

	//if the value of the Request type field is Facilities, add
	//two Facilities choices and other to the what needed field choice list
	if(newValue == 'facilities')
		{
			g_form.addOption('u_choice_4','facilities1','Facilities 1');
			g_form.addOption('u_choice_4','facilities2','Facilities 2');
			g_form.addOption('u_choice_4','other','Other');
		}

	//if the value of the Request type field is Legal, add
	//two Legal choices and other to the what needed field choice list
	if(newValue == 'legal')
		{
			g_form.addOption('u_choice_4','legal1','Legal 1');
			g_form.addOption('u_choice_4','legal2','Legal 2');
			g_form.addOption('u_choice_4','other','Other');

		}

	// If the form is loading and it is not a new record, set the u_what_needed value to the
	// value from the record before it was loaded
	if(isLoading && !g_form.isNewRecord())
		{
			g_form.setValue('u_choice_4',whatneeded);
		}


}
